import React from 'react';
import './App.css';

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      counte :0
    }
  }
  increment =()=>{
    this.setState({counte:this.state.counte +1})
  }
  render (){
    return (
    <div className="App">
      <button className="increment" onClick={this.increment} >+</button>
    </div>
    )
  }


}

export default App;
